mapper.update:
UPDATE input JOIN transaction ON transaction.tran_id = input.tran_id JOIN supplier ON supplier.sup_code = transaction.sup_code JOIN plan ON plan.plan_id = transaction.plan_id JOIN item ON item.item_id = plan.item_id JOIN inventory ON inventory.inven_id = supplier.inven_id
supplier.name = #{supName}, inventory.name = #{invenName}, item.name = #{itemName}, plan.qty = #{qty},
WHERE input.input_id = #{inputId}
